import React, { useEffect, useState } from 'react';
import { getTime } from '../apiHandlers/clockapi';

export default function CreateClock() {
  const [time, setTime] = useState('');

  useEffect(() => {
    async function fetchTime() {
      const data = await getTime();
      setTime(data.time); // set time from API response
    }
    fetchTime();
  }, []);

  return (
    <div className="clock">
      <h2 id="clock" style="color: white;">{time || 'Loading time...'}</h2>
    </div>
  );
}
