import axios from 'axios';

export async function getWeather() {
  const {latitude, longitude} = await getLocation();
  const response = await axios.get("api.openweathermap.org/data/2.5/forecast?lat=${latitude}&lon=${longitude}&appid={8c3dd5e4f4b2b797fa46d9c331c867d9}")
  console.log(response.data);
  return response.data;
};

async function getLocation() {
  const location = await axios.get("http://api.openweathermap.org/geo/1.0/direct?q={Pearland},{020},{ISO3166-2}&appid={8c3dd5e4f4b2b797fa46d9c331c867d9}");
  return {
  latitude: location.lat,
  longitude: location.lon
  };
};
