import axios from 'axios';

export async function getWeather() {
  const loc = getLocation();
  const response = await axios.get("api.openweathermap.org/data/2.5/forecast?lat=${loc.latitude}&lon=${loc.longitude}&appid={8c3dd5e4f4b2b797fa46d9c331c867d9}")
};

export async function getLocation() {
  const location = await axios.get("http://api.openweathermap.org/geo/1.0/direct?q={Pearland},{020},{ISO 3166-2:US&appid={API key}");
  const latitude = location.data[0].lat
  const longitude = location.data[0].lon
};
